import { View, Text, FlatList, Image, ScrollView} from 'react-native'
import React, {useState} from 'react'
import { CircleButton, DirectButton } from '../Button'
import {DirectGame} from "./utils"

interface props  {
    data : {
      name : string, 
      machines : number, 
      tables : number, 
      hotel : boolean, 
      restaurant : boolean, 
      parisSportif : boolean, 
      poker : boolean,
      horaire : number[],
      jeux :  string[], 
      jeuxId : number[], 
      eventsId : number[], 
      tournoisId : number[], 
      desc : string,
      region : string, 
      adresse : string, 
      id: number, 
  
  
    },
  
    setDisplayCasino : (input : boolean) => void,
  
    setData : (input : {
      name : string, 
      machines : number, 
      tables : number, 
      hotel : boolean, 
      restaurant : boolean, 
      parisSportif : boolean, 
      poker : boolean,
      horaire : number[],
      jeux :  string[], 
      jeuxId : number[], 
      eventsId : number[], 
      tournoisId : number[], 
      desc : string,
      region : string, 
      adresse : string, 
      id: number, 
    }) => void, 
  }

const CasinoView = ({data} : {data : props["data"]}) => {

    return( 
    <ScrollView 
    showsVerticalScrollIndicator={false}
    style={{
        width:"100%", 
        flex:1, 
        
    }}>

        <View style={{
          justifyContent:"space-between", 
          alignItems:"center", 
        }}>
          <Text 
          style={{
              fontFamily:"Barlow_800ExtraBold", 
              fontSize:20, 
              marginBottom:10
          }}
          >{data.name}</Text>

          <View
          style={{
            width: "100%",
            height: 200,
            alignItems:"center" , 
            marginBottom:10
          }}>
            <Image
                  source={require("../../assets/Casino.jpg")}
                  resizeMode="contain"
                  style={{ width: "80%", height: "100%", 
                  borderRadius: 30}}
                />
          </View>


          {data.region &&
            <View 
            style={{
            flexDirection:"row", 
            height:30,
            marginTop:10,
            alignItems:"center", 
            width:"80%", 
            justifyContent:"flex-start", 
            }}>
                <Image
                    source={require("../../assets/icons8-casino-100.png")}
                    resizeMode="contain"
                    style={{height:30, width:"20%"}}
                    />


              <Text
              style={{
                textAlign:"justify", 
                fontSize:12, 
                width:"80%", 
                fontFamily: 'Barlow_500Medium',
              }}>{data.name} , {data.adresse}, {data.region}</Text>

            </View>

          }

          {data.machines &&
            <View 
            style={{
            flexDirection:"row", 
            height:30,
            alignItems:"center", 
            width:"80%", 
            justifyContent:"flex-start",
            marginTop:10}}>
                <Image
                    source={require("../../assets/icons8-slot-64.png")}
                    resizeMode="contain"
                    style={{height:"100%",width:"20%"}}
                    />


              <Text
              style={{
                textAlign:"justify", 
                fontSize:12, 
                fontFamily: 'Barlow_500Medium',
              }}>{data.machines} machines</Text>

            </View>

          }


          {data.tables &&
            <View 
            style={{
            flexDirection:"row", 
            height:30,
            marginTop:10,
            alignItems:"center", 
            width:"80%", 
            justifyContent:"flex-start",}}>
                <Image
                    source={require("../../assets/icons8-table-60.png")}
                    resizeMode="contain"
                    style={{height:"100%",width:"20%"}}
                    />


              <Text
              style={{
                textAlign:"justify", 
                fontSize:12, 
                fontFamily: 'Barlow_500Medium',
              }}>{data.tables} tables</Text>

            </View>

          }
          
          {data.hotel &&
            <View 
            style={{
            flexDirection:"row", 
            height:30,
            marginTop:10,
            alignItems:"center",
            width:"80%", 
            justifyContent:"flex-start",}}>
                <Image
                    source={require("../../assets/icons8-hotel-48.png")}
                    resizeMode="contain"
                    style={{height:"100%",width:"20%"}}
                    />


              <Text
              style={{
                textAlign:"justify", 
                fontSize:12, 
                fontFamily: 'Barlow_500Medium',
              }}>Hotel</Text>

            </View>

          }
          {data.restaurant &&
            <View 
            style={{
            flexDirection:"row", 
            height:30,
            marginTop:10,
            alignItems:"center", 
            width:"80%", 
            justifyContent:"flex-start"
            }}>
                <Image
                    source={require("../../assets/icons8-restaurant-30.png")}
                    resizeMode="contain"
                    style={{height:"100%",width:"20%"}}
                    />


              <Text
              style={{
                textAlign:"justify", 
                fontSize:12, 
                fontFamily: 'Barlow_500Medium',
              }}>Restaurant</Text>

            </View>

          }

        {data.parisSportif &&
              <View 
              style={{
              flexDirection:"row", 
              height:30,
              marginTop:10,
              alignItems:"center", 
              width:"80%", 
              justifyContent:"flex-start"
              }}>
                  <Image
                      source={require("../../assets/icons8-bet-on-sport-48.png")}
                      resizeMode="contain"
                      style={{height:"100%",width:"20%"}}
                      />


                <Text
                style={{
                  textAlign:"justify", 
                  fontSize:12, 
                  fontFamily: 'Barlow_500Medium',
                }}>Paris Sportif</Text>

              </View>

            }

          {data.poker &&
            <View 
            style={{
            flexDirection:"row", 
            height:30,
            marginTop:10,
            alignItems:"center", 
            width:"80%", 
            justifyContent:"flex-start"
            }}>
                <Image
                    source={require("../../assets/icons8-poker-64.png")}
                    resizeMode="contain"
                    style={{height:"100%",width:"20%"}}
                    />


              <Text
              style={{
                textAlign:"justify", 
                fontSize:12, 
                fontFamily: 'Barlow_500Medium',
              }}>Poker</Text>

            </View>

          }


          {data.horaire &&
            <View 
            style={{
            flexDirection:"row", 
            height:30,
            marginTop:10,
            alignItems:"center", 
            width:"80%", 
            justifyContent:"flex-start"
            }}>
                <Image
                    source={require("../../assets/icons8-date-65.png")}
                    resizeMode="contain"
                    style={{height:"100%",width:"20%"}}
                    />


              <Text
              style={{
                textAlign:"justify", 
                fontSize:12, 
                fontFamily: 'Barlow_500Medium',
              }}>{data.horaire[0]} h - {data.horaire[1]} h</Text>

            </View>

          }

        

          {data.jeux.length !== 0  &&
          <View style={{
            flexDirection:"row", 
            marginTop: 15, 
            justifyContent:"flex-start", 
            paddingLeft:10,
            width:"80%"

          }}>
            <Text  style={{
                        fontSize:12, 
                        fontFamily: 'Barlow_500Medium'}}>Jeux : </Text>
            {data.jeux.map((item) => {
              return  <Text style={{
                        fontSize:12, 
                        marginRight : 10, 
                        fontFamily: 'Barlow_500Medium'}}>{item},</Text> })}
          </View>


          }




          {/* <Text>{data.eventsId}</Text> */}
          {/* <Text>{data.tournoisId}</Text> */}

          


          {data.eventsId.length !== 0 &&
          <View style={{
            marginTop: 15, 
            justifyContent:"flex-start", 
            paddingLeft:10,
            width:"80%"
          }}>

            <Text style={{
              borderBottomWidth:1, 
              marginBottom: 5
            }}>Next events :</Text>

              {
                data.eventsId.map((item) => {
                  return (
                  <View style={{
                    flexDirection:'row', 
                    justifyContent:"space-evenly"
                  }}>
                  <Text>mcgregor vs khabib</Text>
                  <Text>12 juillet</Text>
                  </View>
                  )
                })
              }

          </View>
          }


          {data.tournoisId.length !== 0 &&
          <View style={{
            marginTop: 15, 
            justifyContent:"flex-start", 
            paddingLeft:10,
            width:"80%"
          }}>

            <Text style={{
              borderBottomWidth:1,
              marginBottom: 5,  
            }}>Next tournois :</Text>

            {
              data.tournoisId.map((item) => {
                return (
                  <View style={{
                    flexDirection:'row', 
                    justifyContent:"space-evenly"
                  }}>
                  <Text>Poker</Text>
                  <Text>Blind : 100 â‚¬</Text>
                  <Text>12 juillet</Text>
                  </View>
                )
              })
            }

          </View>
          }


          {data.desc &&
            <View 
            style={{
            flexDirection:"row", 
            marginTop:10,
            alignItems:"center", 
            width:"80%", 
            justifyContent:"flex-start"
            }}>
                


              <Text
              style={{
                textAlign:"justify", 
                fontSize:14, 
                fontFamily: 'Barlow_500Medium',
              }}>{data.desc}</Text>

            </View>

          }





        <View style={{height:200}}/>
        </View>
    </ScrollView>
    ); 
}


const DirectView = ({data} : {data : props["data"]}) => {
  return (
    <View style={{
      width:"80%", 
      height:"80%", 
      alignSelf:"center", 
      alignItems:"center"
  }}>
      <Text 
      style={{
          fontFamily:"Barlow_800ExtraBold", 
          fontSize:20
      }}
      >LIVE</Text>

      <FlatList
      data={data.jeuxId}
      style={{marginTop:"10%", width:"100%"}}
      renderItem={({ item }) => <DirectGame id={item}/>}
      keyExtractor={(item) => String(item)}
      showsVerticalScrollIndicator={false}
      ListFooterComponent={<View style={{height:50}}/>}
    />
  </View>

  )
}

const CasinoScreen = ({data, setDisplayCasino,  setData} : props) => {

    const [direct, setDirect] = useState(false)
    const [inputText, setInputText] = useState("DIRECT")


    const handleQuitClick = () => {
    setDisplayCasino(false)
    setData({
        name : "", 
        machines : 0, 
        tables : 0, 
        hotel : true, 
        restaurant : true, 
        parisSportif : true, 
        poker : true,
        horaire : [1],
        jeux : [""], 
        jeuxId : [1], 
        eventsId : [1], 
        tournoisId : [1], 
        desc : "",
        region : "", 
        adresse : "", 
        id: 0
    
      })
    }

    const handleDirectClick = () => {
        if (!direct){
            setInputText("INFO")
        }
        else {
            setInputText("DIRECT")
        }
        setDirect(prev => !prev)
    }
  return (
    <View style={{
        height:"90%", 
        padding: 10, 
        flex:1
    }}>
        
        {direct
        ? <DirectView data={data}/>
        : <CasinoView data={data}/>

        }

        <CircleButton handlePress={handleQuitClick} img={require("../../assets/goBack.png")}/>
        <DirectButton handlePress={handleDirectClick} input={inputText}/>
    </View>
  )
}

export default CasinoScreen
