import { View, Text,Image } from 'react-native'
import React from 'react'
import { COLORS } from '../../constants';

export const DirectGame = ({id} : {id : number}) => {
    return (
        <View 
        style={{
        marginHorizontal: 20, 
        height : 70, 
        backgroundColor : COLORS.BackgroundOne, 
        marginBottom:10, 
        borderRadius: 20,  
    }}>
        

        

        
       {[1,5,9,13,17].includes(id) &&
        <View style={{
            height: "100%", 
            width:"100%", 
            flexDirection:"row", 
            justifyContent:"space-evenly", 
            alignItems:"center"

        }}>
            <Image
            source={require("../../assets/icons8-poker-53.png")}
            resizeMode="contain"
            style={{height:"60%"}}
            />

            <Text>Poker</Text>
            <Text>blind : 5€</Text>
        </View>
       }
       {[2,6,10,14,18].includes(id) &&

       <View style={{
        height: "100%", 
        width:"100%", 
        flexDirection:"row", 
        justifyContent:"space-evenly", 
        alignItems:"center"

    }}>
            <Image
            source={require("../../assets/icons8-european-roulette-48.png")}
            resizeMode="contain"
            style={{height:"60%"}}
            />
            <Text>roulette</Text>
            <Text>OPEN</Text>
        </View>
       }
       {[3,7,11,15,19].includes(id) &&
        <View style={{
            height: "100%", 
            width:"100%", 
            flexDirection:"row", 
            justifyContent:"space-evenly", 
            alignItems:"center"

        }}>
            <Image
            source={require("../../assets/icons8-slot-64.png")}
            resizeMode="contain"
            style={{height:"60%"}}
            />
            <Text>Slot</Text>
            <Text>6000 €</Text>
        </View>
       }

        {[4,8,12,16,20].includes(id) &&
        <View style={{
            height: "100%", 
            width:"100%", 
            flexDirection:"row", 
            justifyContent:"space-evenly", 
            alignItems:"center"

        }}>
            <Image
            source={require("../../assets/icons8-table-60.png")}
            resizeMode="contain"
            style={{height:"60%"}}
            />
            <Text>BlackJack</Text>
            <Text>OPEN</Text>
        </View>
       }



    </View>
    )
};
