import { View, Text,Image } from 'react-native'
import React, {useState} from 'react'
import { COLORS } from '../../constants';


export const CasinosInfo = ({name, adresse, region} : {name : String, adresse : String, region : String}) => {
    return (
        <View
        style={{
            width:"100%", 
            flexDirection:"row", 
            height: 50 ,
            justifyContent:'flex-start',
        }}>
            <View style={{
                width:"30%", 
            }}>
            <Image
              source={require("../../assets/icons8-casino-100.png")}
              resizeMode="contain"
              style={{height: "100%", alignSelf:"center"}}
            />
            </View>

            <Text style={{
                width: "70%",
                alignSelf:"center",
                textAlign:"justify", 
                fontFamily: 'Barlow_500Medium',

            }}> {name} , {adresse} , {region}</Text>



        </View>
    );
}

export const Description = ({description} : {description : String}) => {

    const [text, setText] = useState(description.slice(0, 100))
    const [readMore, setReadMore] = useState(false);

    return (
        <View
        style={{
            width:"100%", 
            paddingBottom:10,
        }}>
            <Text
            style={{
                fontFamily: 'Barlow_500Medium',
                textAlign:"justify"
            }}>

            <Text>{text}</Text>
            { description.length > 100 &&
            <>
            <Text>{!readMore  && "..."}</Text>
            <Text
              style={{}}
              onPress={() => {
                if (!readMore) {
                  setText(String(description));
                  setReadMore(true);
                } else {
                  setText(description.slice(0, 100));
                  setReadMore(false);
                }
              }}
            >
              {readMore ? " Show Less" : " Read More"}
            </Text>
            </>
            }
        </Text>
        </View>
    );
}

export const Horaire = ({date} : {date : String}) => {

    return (
    <View
    style={{
        width:"100%", 
        flexDirection:"row", 
        height: 50 ,
        justifyContent:'flex-start',
            }}>
        <View style={{
            width:"30%", 
        }}>
        <Image
          source={require("../../assets/icons8-date-65.png")}
          resizeMode="contain"
          style={{height: "80%", alignSelf:"center", marginTop:"10%"}}
        />
        </View>

        <Text style={{
            width: "70%",
            alignSelf:"center",
            textAlign:"justify", 
            fontFamily: 'Barlow_500Medium',

        }}> Date : {date}</Text>

    </View>

    )

}





const EventInfo = ({data} : { data : {salle:String, adresse:String, 
    description:String,  date:String, region: String
    }}) => {
      return (
        <View
          style={{
            width: "100%",
            backgroundColor:COLORS.BackgroundTwo, 
            borderBottomRightRadius: 20, 
            borderBottomLeftRadius: 20, 
            paddingHorizontal: 10,
            paddingTop: 2
          }}
        >  
    
          {data.salle && <CasinosInfo name={data.salle} adresse={data.adresse} region={data.region}/>}
          {data.date && <Horaire date={data.date}/>}
          {data.description && <Description description={data.description}/>}
    
        </View>
      )
    }

export default EventInfo
