import { View, Text, Image, TouchableOpacity, ImageSourcePropType } from 'react-native'
import React, {useState} from 'react'
import { COLORS, FONT, SIZES} from '../constants'
import { useNavigation } from "@react-navigation/native";
import {StackNavigationProp} from '@react-navigation/stack';
import { RootStackParamList } from "../screens";


interface propsHomeMenuCard {
    data : { img : ImageSourcePropType, 
    title : string, 
    right? : number, 
    bottom? : number, 
    color? :string, 
    navigationPage : keyof RootStackParamList }
}

type authScreenProp = StackNavigationProp<RootStackParamList>;

export  const HomeMenuCard = ({data}  : propsHomeMenuCard) => {

  const navigation = useNavigation<authScreenProp>();



  return (
    <TouchableOpacity
    delayPressIn={50} 
    onPress={() => navigation.navigate(data.navigationPage)}

    style={{
      backgroundColor: COLORS.LightPurple,
      borderRadius: 32,
      margin: 20,
      borderColor: COLORS.DarkPurple,
      borderWidth:2,
    }}>
      <View
      style={{
        width: "100%",
        height: 200,
        justifyContent:"center", 
      }}>
        <Image
              source={data.img}
              resizeMode="center"
              style={{ width: "100%", height: "100%", borderRadius: 30}}
            />

        <View
        style={{
          position:"absolute", 
          alignSelf:"center", 
          right:data.right, 
          bottom:data.bottom, 
        }}>
          <Text
          style={{
            fontFamily: FONT.TitleBold, 
            fontSize: SIZES.TilteSizeBig,
            letterSpacing: SIZES.TitleSpacing,
            color: data.color ? data.color : COLORS.White,
            textAlign: "center",
          }}>{data.title}</Text>
        </View>

      </View>
      
    </TouchableOpacity>
  )
}



interface propsUserInfoCard {
  data : {input : string, context : string}
  handleRemove : (input : string) => void, 

}

export const UserInfoCard = ({data, handleRemove} : propsUserInfoCard)  => {
  
  const handleOnPress = () =>  {
      handleRemove(data.context)
  }

  return (
  <View style = {{
      backgroundColor:"#fffafa", 
      borderWidth:1, 
      borderColor:COLORS.Black, 
      borderRadius: 5, 
      padding: 5,
      marginTop:10, 
      flexDirection:"row"

      }}>

      <View style={{
          width: "80%"
      }}>
          <Text style={{
              paddingLeft: 5, 
          }}>{data.input}</Text>
      </View>

      <TouchableOpacity
      onPress={handleOnPress}
      style={{
          width:"20%", 
      }}>
          <Image
              source={require("../assets/icons8-multiply-60.png")}
              resizeMode="contain"
              style={{height:20}}
              />
      </TouchableOpacity>
      
  </View>

  ); 
}

interface propsUserFollowingsCard {
  data : {name : string, feed: string, id : number}
  handleRemove : (id : number) => void, 

}

export const UserFollowingsCard = ({data, handleRemove} : propsUserFollowingsCard) => {
  
  
      const handleOnPress = () => {
          handleRemove(data.id)
      }
      return (
          
          <View style = {{
              backgroundColor:"#fffafa", 
              borderRadius: 5, 
              borderWidth:1, 
              padding: 5,
              marginHorizontal:5, 
              marginTop:10, 
              flexDirection:"row"
      
              }}>
      
              <View style={{
                  width: "80%", 
                  flexDirection:"row", 
              }}> 
                  <Text style={{
                      paddingLeft: 5, 
                      width:"50%", 
                  }}>{data.name}</Text>
  
  
                  <Text style={{
                      paddingLeft: 5, 
                      width:"50%"
                  }}>{data.feed}</Text>
              </View>
      
              <TouchableOpacity
              onPress={handleOnPress}
              style={{
                  width:"20%", 
              }}>
                  <Image
                      source={require("../assets/icons8-multiply-60.png")}
                      resizeMode="contain"
                      style={{height:20}}
                      />
              </TouchableOpacity>
              
          </View>
      ); 
  }
