import { View, Text, FlatList} from 'react-native'
import React, {useState} from 'react'
import {UserFollowingsCard} from '../Cards'







const UserFollowings = () => {

    const [data, setData] = useState([{
        name: "Circus", 
        feed:'machines', 
        id:1
    }, {
        name: "Circus", 
        feed:'events', 
        id:2
        
    }, {
        name: "Barrière", 
        feed:'tables', 
        id:3
    }, {
        name: "Joa", 
        feed:'machines', 
        id:4
    }, {
        name: "Joa", 
        feed:'tournois', 
        id:5
    }, 
    {
        name: "Circus", 
        feed:'machines', 
        id:6
    }, {
        name: "Circus", 
        feed:'events', 
        id:7
        
    }, {
        name: "Barrière", 
        feed:'tables', 
        id:8
    }, {
        name: "Joa", 
        feed:'machines', 
        id:9
    }, {
        name: "Joa", 
        feed:'tournois', 
        id:10
    }
    
    ])

  const handleRemove = (id : number) => {

    const newData = data.filter((item) => item.id !== id)
    setData(newData)
  }






    
  return (
    <View style={{
        width:"80%", 
        alignSelf:"center", 
        borderWidth:1, 
        borderRadius:10, 
        height:250, 

    }}>
        <FlatList
            data={data}
            renderItem={({ item }) => <UserFollowingsCard data={item} handleRemove={handleRemove}/>}
            keyExtractor={(item) => String(item.id)}
            showsVerticalScrollIndicator={false}
            ListHeaderComponent={<Text style={{alignSelf:'center', marginTop:5
        }}> Vous suivez actuellement : </Text>}
            ListFooterComponent={<View style={{height:100}}/>}
          />
    </View>
  )
}

export default UserFollowings
