import { View, Image, TextInput } from 'react-native'
import React, {Dispatch, SetStateAction} from 'react'
import { COLORS } from '../../constants'
import { Picker } from "@react-native-picker/picker";



const PokerHeaderSearch = ({onSearch, dates,date, setDate} : 
    {onSearch : (input : String) => void, 
    dates : string[],
    date : string, 
    setDate: Dispatch<SetStateAction<string>> }) => {
    


    return (
    <View     
    style={{
        marginHorizontal:10, 
        marginTop: 30
    }}>
        <View
          style={{
            width: "80%",
            height:50, 
            borderRadius: 20,
            backgroundColor:COLORS.BackgroundTwo, 
            flexDirection: "row",
            alignItems: "center",
            alignSelf: "center", 
            paddingHorizontal: 10, 
            paddingVertical: 5, 
          }}
        >
            <Image
            source={require("../../assets/icons8-search-60.png")}
            style={{ width: 20, height: 20}}
            />
            <View style={{width:10}}/>

            <TextInput
            placeholder="Search"
            onChangeText={onSearch}
            style={{ flex: 1, fontSize: 20, fontFamily:"Barlow_300Light_Italic" }}
            />

        </View>
        <Picker
        selectedValue={date}
        onValueChange={(value, index) => setDate(value)}
        mode="dropdown" // Android only
        style={{width:"80%",
            alignSelf:"center",
            marginTop:10}}
        >
            <Picker.Item label="Select a date" value="Unknown" />
            {dates.map((date, key) => {
                return <Picker.Item label={date} value={date} key={key}/>
            })}
        
      </Picker>
    </View>
  )
}

export default PokerHeaderSearch
