import { View, Image } from 'react-native'
import React from 'react'
import PokerInfo from "./PokerInfo"

interface props  {
    item : {id:number,
        type:string,
        salle : string,
        adresse : string,
        region: string,
        description : string, 
        date : string, 
        blind : number,
        img : string}
}
const PokerCard = ({item} :  props) => {
    return (
        <View
        style={{
          margin: 20,
    
        }}>
          
          <View
          style={{
            width: "100%",
            height: 200,
            justifyContent:"center", 
          }}>
            <Image
                  source={require("../../assets/PokerCover.jpg")}
                  resizeMode="contain"
                  style={{ width: "100%", height: "100%", borderTopLeftRadius: 30, 
                  borderTopRightRadius: 30}}
                />
          </View>
          <PokerInfo data={item}/>
        </View>
  )
}

export default PokerCard
