import { View, FlatList } from 'react-native'
import React, {useEffect, useState} from 'react'
import { CircleButton } from '../Button'
import PokerHeaderSearch from './PokerHeaderSearch'
import { PokerData } from '../../constants'
import PokerCard from './PokerCard'

const RegularView = () => {

    const dates = ["Monday", "Tuesday", "Wednesday", "Thursday","Friday", "Saturday", "Sunday"]
    const [date, setDate] = useState('Unknown')
    


    const filterData = (data : {id:number,
        type:string,
        salle : string,
        adresse : string,
        region: string,
        description : string, 
        date : string, 
        blind : number,
        img : string}[]) => {
        const filteredData = data.filter((item) => item.type === "Regular")
        return filteredData
    }

    const [pokerData, setPokerData] = useState(filterData(PokerData))
    const [pokerData1, setPokerData1] = useState(filterData(PokerData))
    const [pokerData2, setPokerData2] = useState(filterData(PokerData))

    const handleSearch = (value : String) => {


        if (value.length === 0) {
            setPokerData1(filterData(PokerData)); 

            if(filterData(PokerData).length === pokerData2.length) {
                setPokerData(filterData(PokerData));
            }
            else {
                setPokerData(pokerData2);
            }
        }
    
        else {
    
            const filteredData = pokerData2.filter((item) =>
              (item.region.toLowerCase().includes(value.toLowerCase()) ||
              item.salle.toLowerCase().includes(value.toLowerCase()))
              
            );
    
            if (filteredData.length === 0) {
                setPokerData([]);

            } else {
                setPokerData(filteredData);
                setPokerData1(filteredData);
            }
        }

   
    };
    

    
    
    useEffect(() => {
        if (date !== 'Unknown'){

        const filteredData = pokerData1.filter((item) =>
            item.date.toLowerCase().includes(date.toLowerCase())
        );

        if (filteredData.length === 0) {
            setPokerData([]);
        } else {
            setPokerData(filteredData);
            setPokerData2(filteredData);
        }

        }

        if(date === 'Unknown'){
            setPokerData2(filterData(PokerData)); 

            if(filterData(PokerData).length === pokerData1.length) {
                setPokerData(filterData(PokerData));
            }
            else {
                setPokerData(pokerData1);
            }
        }
    
      }, [date])



    return (
        <View style={{
            width:"100%", 
            height: "100%", 
        }}>
            <FlatList
            data={pokerData}
            renderItem={({ item }) => <PokerCard item={item}/>}
            keyExtractor={(item) => String(item.id)}
            showsVerticalScrollIndicator={false}
            ListHeaderComponent={<PokerHeaderSearch  dates={dates} onSearch={handleSearch} date={date} setDate={setDate} />}
            ListFooterComponent={<View style={{height:200}}/>}
        />
        </View>
    )
}

const EventView = () => {


    const DatesExtractor = (data : {id:number,
        type:string,
        salle : string,
        adresse : string,
        region: string,
        description : string, 
        date : string, 
        blind : number,
        img : string}[]) => {
        var dates = [""]
        data.map((item) => {
            if(!dates.includes(item.date)){
                dates.push(item.date)
    
            }
        })

        return dates.slice(1)
      }

    
    


    const filterData = (data : {id:number,
        type:string,
        salle : string,
        adresse : string,
        region: string,
        description : string, 
        date : string, 
        blind : number,
        img : string}[]) => {
        const filteredData = data.filter((item) => item.type === "Exceptional")
        return filteredData
    }

    const [pokerData, setPokerData] = useState(filterData(PokerData))
    const [pokerData1, setPokerData1] = useState(filterData(PokerData))
    const [pokerData2, setPokerData2] = useState(filterData(PokerData))
    const [date, setDate] = useState('Unknown')
    const dates = DatesExtractor(filterData(PokerData))

    const handleSearch = (value : String) => {


        if (value.length === 0) {
            setPokerData1(filterData(PokerData)); 

            if(filterData(PokerData).length === pokerData2.length) {
                setPokerData(filterData(PokerData));
            }
            else {
                setPokerData(pokerData2);
            }
        }
    
        else {
    
            const filteredData = pokerData2.filter((item) =>
              (item.region.toLowerCase().includes(value.toLowerCase()) ||
              item.salle.toLowerCase().includes(value.toLowerCase()))
              
            );
    
            if (filteredData.length === 0) {
                setPokerData([]);

            } else {
                setPokerData(filteredData);
                setPokerData1(filteredData);
            }
        }

   
    };
    

    
    
    useEffect(() => {
        if (date !== 'Unknown'){

        const filteredData = pokerData1.filter((item) =>
            item.date.toLowerCase().includes(date.toLowerCase())
        );

        if (filteredData.length === 0) {
            setPokerData([]);
        } else {
            setPokerData(filteredData);
            setPokerData2(filteredData);
        }

        }

        if(date === 'Unknown'){
            setPokerData2(filterData(PokerData)); 

            if(filterData(PokerData).length === pokerData1.length) {
                setPokerData(filterData(PokerData));
            }
            else {
                setPokerData(pokerData1);
            }
        }
    
      }, [date])



    return (
        <View style={{
            width:"100%", 
            height: "100%", 
        }}>
            <FlatList
            data={pokerData}
            renderItem={({ item }) => <PokerCard item={item}/>}
            keyExtractor={(item) => String(item.id)}
            showsVerticalScrollIndicator={false}
            ListHeaderComponent={<PokerHeaderSearch  dates={dates} onSearch={handleSearch} date={date} setDate={setDate} />}
            ListFooterComponent={<View style={{height:200}}/>}
        />
        </View>
    )
}


const TournamentView = ({handleQuitClick, state} : {handleQuitClick : () => void, state : number}) => {
  return (
    <View style={{
        height:"90%", 
        padding: 10, 
    }}>
      {state===1
        &&<RegularView/>
      }
      {state ===2
      &&<EventView/>

      }
      <CircleButton handlePress={handleQuitClick} img={require("../../assets/goBack.png")}/>
    </View>
  )
}

export default TournamentView
